CREATE TABLE summaries (
    id INT AUTO_INCREMENT PRIMARY KEY,
    act VARCHAR(10),
    scene VARCHAR(10),
    summary_text TEXT
);

CREATE TABLE analyses (
    id INT AUTO_INCREMENT PRIMARY KEY,
    act VARCHAR(10),
    scene VARCHAR(10),
    analysis_text TEXT
);

CREATE TABLE poetic_devices (
    id INT AUTO_INCREMENT PRIMARY KEY,
    act VARCHAR(10),
    scene VARCHAR(10),
    device_name VARCHAR(100),
    device_description TEXT
);

CREATE TABLE important_questions (
    id INT AUTO_INCREMENT PRIMARY KEY,
    act VARCHAR(10),
    scene VARCHAR(10),
    question_text TEXT
);





